SELECT MIN("t1415"."name") AS "from_company", MIN("movie_companies114"."note") AS "production_note", MIN("t1418"."title") AS "movie_based_on_book" FROM (SELECT * FROM "company_name" WHERE "country_code" <> '[pl]' AND ("name" LIKE '20th Century Fox%' OR "name" LIKE 'Twentieth Century Fox%')) AS "t1415" CROSS JOIN (SELECT * FROM "company_type" WHERE "kind" <> 'production companies') AS "t1416" CROSS JOIN (SELECT * FROM "keyword" WHERE "keyword" IN ('based-on-novel', 'revenge', 'sequel')) AS "t1417" CROSS JOIN "link_type" AS "link_type35" INNER JOIN "movie_companies" AS "movie_companies114" ON "t1416"."id" = "movie_companies114"."company_type_id" AND "t1415"."id" = "movie_companies114"."company_id" INNER JOIN "movie_keyword" AS "movie_keyword114" ON "t1417"."id" = "movie_keyword114"."keyword_id" AND "movie_companies114"."movie_id" = "movie_keyword114"."movie_id" INNER JOIN "movie_link" AS "movie_link35" ON "link_type35"."id" = "movie_link35"."link_type_id" AND "movie_keyword114"."movie_id" = "movie_link35"."movie_id" AND "movie_companies114"."movie_id" = "movie_link35"."movie_id" INNER JOIN (SELECT * FROM "title" WHERE "production_year" > 1950) AS "t1418" ON "movie_link35"."movie_id" = "t1418"."id" AND "movie_keyword114"."movie_id" = "t1418"."id" AND "movie_companies114"."movie_id" = "t1418"."id"