SELECT MIN("t80107"."name") AS "movie_company", MIN("t80107"."info2") AS "rating", MIN("t80108"."title") AS "drama_horror_movie" FROM (SELECT * FROM (SELECT * FROM (SELECT * FROM (SELECT * FROM "company_name" WHERE "country_code" = '[us]') AS "t80099", (SELECT * FROM "company_type" WHERE "kind" = 'production companies') AS "t80100", (SELECT * FROM "info_type" WHERE "info" = 'genres') AS "t80101", (SELECT * FROM "info_type" WHERE "info" = 'rating') AS "t80102", "movie_companies" AS "movie_companies4728" WHERE "t80100"."id" = "movie_companies4728"."company_type_id" AND "t80099"."id" = "movie_companies4728"."company_id") AS "t80103", (SELECT * FROM "movie_info" WHERE "info" IN ('Drama', 'Horror')) AS "t80104" WHERE "t80104"."info_type_id" = "t80103"."id1" AND "t80103"."movie_id" = "t80104"."movie_id") AS "t80105", (SELECT * FROM "movie_info_idx" WHERE "info" > '8.0') AS "t80106" WHERE "t80106"."info_type_id" = "t80105"."id2" AND "t80105"."movie_id" = "t80106"."movie_id" AND "t80105"."movie_id0" = "t80106"."movie_id") AS "t80107", (SELECT * FROM "title" WHERE "production_year" >= 2005 AND "production_year" <= 2008) AS "t80108" WHERE "t80108"."id" = "t80107"."movie_id0" AND "t80108"."id" = "t80107"."movie_id1" AND "t80108"."id" = "t80107"."movie_id"