SELECT MIN("t2985"."name") AS "movie_company", MIN("t2985"."info2") AS "rating", MIN("t2986"."title") AS "mainstream_movie" FROM (SELECT * FROM (SELECT * FROM (SELECT * FROM (SELECT * FROM "company_name" WHERE "country_code" = '[us]') AS "t2977", (SELECT * FROM "company_type" WHERE "kind" = 'production companies') AS "t2978", (SELECT * FROM "info_type" WHERE "info" = 'genres') AS "t2979", (SELECT * FROM "info_type" WHERE "info" = 'rating') AS "t2980", "movie_companies" AS "movie_companies179" WHERE "t2978"."id" = "movie_companies179"."company_type_id" AND "t2977"."id" = "movie_companies179"."company_id") AS "t2981", (SELECT * FROM "movie_info" WHERE "info" IN ('Drama', 'Family', 'Horror', 'Western')) AS "t2982" WHERE "t2982"."info_type_id" = "t2981"."id1" AND "t2981"."movie_id" = "t2982"."movie_id") AS "t2983", (SELECT * FROM "movie_info_idx" WHERE "info" > '7.0') AS "t2984" WHERE "t2984"."info_type_id" = "t2983"."id2" AND "t2983"."movie_id" = "t2984"."movie_id" AND "t2983"."movie_id0" = "t2984"."movie_id") AS "t2985", (SELECT * FROM "title" WHERE "production_year" >= 2000 AND "production_year" <= 2010) AS "t2986" WHERE "t2986"."id" = "t2985"."movie_id0" AND "t2986"."id" = "t2985"."movie_id1" AND "t2986"."id" = "t2985"."movie_id"