SELECT MIN("t73267"."name") AS "producing_company", MIN("movie_info_idx3084"."info") AS "rating", MIN("title7352"."title") AS "movie" FROM (SELECT * FROM "company_name" WHERE "country_code" = '[us]') AS "t73267" CROSS JOIN (SELECT * FROM "company_type" WHERE "kind" = 'production companies') AS "t73268" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'rating') AS "t73269" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'release dates') AS "t73270" CROSS JOIN (SELECT * FROM "kind_type" WHERE "kind" = 'movie') AS "t73271" INNER JOIN "movie_companies" AS "movie_companies4403" ON "t73267"."id" = "movie_companies4403"."company_id" AND "t73268"."id" = "movie_companies4403"."company_type_id" INNER JOIN "movie_info" AS "movie_info3086" ON "t73270"."id" = "movie_info3086"."info_type_id" AND "movie_companies4403"."movie_id" = "movie_info3086"."movie_id" INNER JOIN "movie_info_idx" AS "movie_info_idx3084" ON "t73269"."id" = "movie_info_idx3084"."info_type_id" AND "movie_info3086"."movie_id" = "movie_info_idx3084"."movie_id" AND "movie_companies4403"."movie_id" = "movie_info_idx3084"."movie_id" INNER JOIN "title" AS "title7352" ON "movie_info3086"."movie_id" = "title7352"."id" AND "t73271"."id" = "title7352"."kind_id" AND "movie_companies4403"."movie_id" = "title7352"."id" AND "movie_info_idx3084"."movie_id" = "title7352"."id"