SELECT MIN("t36309"."info") AS "movie_budget", MIN("movie_info_idx1849"."info") AS "movie_votes", MIN("title3588"."title") AS "movie_title" FROM (SELECT * FROM "cast_info" WHERE "note" IN ('(head writer)', '(story editor)', '(story)', '(writer)', '(written by)')) AS "t36306" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'genres') AS "t36307" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'votes') AS "t36308" INNER JOIN (SELECT * FROM "movie_info" WHERE "info" IN ('Action', 'Crime', 'Horror', 'Sci-Fi', 'Thriller', 'War')) AS "t36309" ON "t36306"."movie_id" = "t36309"."movie_id" AND "t36307"."id" = "t36309"."info_type_id" INNER JOIN "movie_info_idx" AS "movie_info_idx1849" ON "t36306"."movie_id" = "movie_info_idx1849"."movie_id" AND "t36309"."movie_id" = "movie_info_idx1849"."movie_id" AND "t36308"."id" = "movie_info_idx1849"."info_type_id" INNER JOIN (SELECT * FROM "name" WHERE "gender" = 'm') AS "t36310" ON "t36306"."person_id" = "t36310"."id" INNER JOIN "title" AS "title3588" ON "t36309"."movie_id" = "title3588"."id" AND "movie_info_idx1849"."movie_id" = "title3588"."id" AND "t36306"."movie_id" = "title3588"."id"