SELECT MIN("t31375"."production_note") AS "production_note", MIN("t31377"."movie_title") AS "movie_title", MIN("t31377"."movie_year") AS "movie_year" FROM (SELECT "t31373"."movie_id", "t31374"."movie_id" AS "movie_id0", MIN("t31373"."production_note") AS "production_note" FROM (SELECT "t31370"."id" AS "id0", "t31372"."movie_id", MIN("t31372"."production_note") AS "production_note" FROM (SELECT "id" FROM "company_type" WHERE "kind" = 'production companies' GROUP BY "id") AS "t31368" CROSS JOIN (SELECT "id" FROM "info_type" WHERE "info" = 'bottom 10 rank' GROUP BY "id") AS "t31370" INNER JOIN (SELECT "movie_id", "company_type_id", MIN("note") AS "production_note" FROM "movie_companies" WHERE "note" NOT LIKE '%(as Metro-Goldwyn-Mayer Pictures)%' GROUP BY "movie_id", "company_type_id") AS "t31372" ON "t31368"."id" = "t31372"."company_type_id" GROUP BY "t31370"."id", "t31372"."movie_id") AS "t31373" INNER JOIN (SELECT "movie_id", "info_type_id" FROM "movie_info_idx" GROUP BY "movie_id", "info_type_id") AS "t31374" ON "t31373"."movie_id" = "t31374"."movie_id" AND "t31373"."id0" = "t31374"."info_type_id" GROUP BY "t31373"."movie_id", "t31374"."movie_id") AS "t31375" INNER JOIN (SELECT "id", MIN("title") AS "movie_title", MIN("production_year") AS "movie_year" FROM "title" WHERE "production_year" > 2000 GROUP BY "id") AS "t31377" ON "t31375"."movie_id" = "t31377"."id" AND "t31375"."movie_id0" = "t31377"."id"