SELECT MIN("t319"."name") AS "movie_company", MIN("t326"."info") AS "rating", MIN("t327"."title") AS "western_violent_movie" FROM (SELECT * FROM "company_name" WHERE "country_code" <> '[us]') AS "t319" CROSS JOIN "company_type" AS "company_type25" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'countries') AS "t320" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'rating') AS "t321" CROSS JOIN (SELECT * FROM "keyword" WHERE "keyword" IN ('blood', 'murder', 'murder-in-title', 'violence')) AS "t322" CROSS JOIN (SELECT * FROM "kind_type" WHERE "kind" IN ('episode', 'movie')) AS "t323" INNER JOIN (SELECT * FROM "movie_companies" WHERE "note" LIKE '%(200%)%' AND "note" NOT LIKE '%(USA)%') AS "t324" ON "company_type25"."id" = "t324"."company_type_id" AND "t319"."id" = "t324"."company_id" INNER JOIN (SELECT * FROM "movie_info" WHERE "info" IN ('American', 'German', 'Germany', 'USA')) AS "t325" ON "t324"."movie_id" = "t325"."movie_id" AND "t320"."id" = "t325"."info_type_id" INNER JOIN (SELECT * FROM "movie_info_idx" WHERE "info" < '7.0') AS "t326" ON "t325"."movie_id" = "t326"."movie_id" AND "t324"."movie_id" = "t326"."movie_id" AND "t321"."id" = "t326"."info_type_id" INNER JOIN "movie_keyword" AS "movie_keyword25" ON "t325"."movie_id" = "movie_keyword25"."movie_id" AND "t326"."movie_id" = "movie_keyword25"."movie_id" AND "t324"."movie_id" = "movie_keyword25"."movie_id" AND "t322"."id" = "movie_keyword25"."keyword_id" INNER JOIN (SELECT * FROM "title" WHERE "production_year" > 2008) AS "t327" ON "t323"."id" = "t327"."kind_id" AND "t325"."movie_id" = "t327"."id" AND "movie_keyword25"."movie_id" = "t327"."id" AND "t326"."movie_id" = "t327"."id" AND "t324"."movie_id" = "t327"."id"