SELECT MIN("t88435"."info") AS "movie_budget", MIN("movie_info_idx3411"."info") AS "movie_votes", MIN("t88436"."name") AS "male_writer", MIN("t88437"."title") AS "violent_movie_title" FROM (SELECT * FROM "cast_info" WHERE "note" IN ('(head writer)', '(story editor)', '(story)', '(writer)', '(written by)')) AS "t88431" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'genres') AS "t88432" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'votes') AS "t88433" CROSS JOIN (SELECT * FROM "keyword" WHERE "keyword" IN ('blood', 'death', 'female-nudity', 'gore', 'murder')) AS "t88434" INNER JOIN (SELECT * FROM "movie_info" WHERE "info" = 'Horror') AS "t88435" ON "t88431"."movie_id" = "t88435"."movie_id" AND "t88432"."id" = "t88435"."info_type_id" INNER JOIN "movie_info_idx" AS "movie_info_idx3411" ON "t88431"."movie_id" = "movie_info_idx3411"."movie_id" AND "t88435"."movie_id" = "movie_info_idx3411"."movie_id" AND "t88433"."id" = "movie_info_idx3411"."info_type_id" INNER JOIN "movie_keyword" AS "movie_keyword4946" ON "t88431"."movie_id" = "movie_keyword4946"."movie_id" AND "t88435"."movie_id" = "movie_keyword4946"."movie_id" AND "movie_info_idx3411"."movie_id" = "movie_keyword4946"."movie_id" AND "t88434"."id" = "movie_keyword4946"."keyword_id" INNER JOIN (SELECT * FROM "name" WHERE "gender" = 'm') AS "t88436" ON "t88431"."person_id" = "t88436"."id" INNER JOIN (SELECT * FROM "title" WHERE "production_year" > 2010 AND "title" LIKE 'Vampire%') AS "t88437" ON "t88435"."movie_id" = "t88437"."id" AND "movie_info_idx3411"."movie_id" = "t88437"."id" AND "t88431"."movie_id" = "t88437"."id" AND "movie_keyword4946"."movie_id" = "t88437"."id"