SELECT MIN("t55313"."info") AS "movie_budget", MIN("movie_info_idx2291"."info") AS "movie_votes", MIN("t55314"."name") AS "male_writer", MIN("title5444"."title") AS "violent_movie_title" FROM (SELECT * FROM "cast_info" WHERE "note" IN ('(head writer)', '(story editor)', '(story)', '(writer)', '(written by)')) AS "t55309" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'genres') AS "t55310" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'votes') AS "t55311" CROSS JOIN (SELECT * FROM "keyword" WHERE "keyword" IN ('blood', 'death', 'female-nudity', 'gore', 'hospital', 'murder', 'violence')) AS "t55312" INNER JOIN (SELECT * FROM "movie_info" WHERE "info" IN ('Action', 'Crime', 'Horror', 'Sci-Fi', 'Thriller', 'War')) AS "t55313" ON "t55309"."movie_id" = "t55313"."movie_id" AND "t55310"."id" = "t55313"."info_type_id" INNER JOIN "movie_info_idx" AS "movie_info_idx2291" ON "t55309"."movie_id" = "movie_info_idx2291"."movie_id" AND "t55313"."movie_id" = "movie_info_idx2291"."movie_id" AND "t55311"."id" = "movie_info_idx2291"."info_type_id" INNER JOIN "movie_keyword" AS "movie_keyword2974" ON "t55309"."movie_id" = "movie_keyword2974"."movie_id" AND "t55313"."movie_id" = "movie_keyword2974"."movie_id" AND "movie_info_idx2291"."movie_id" = "movie_keyword2974"."movie_id" AND "t55312"."id" = "movie_keyword2974"."keyword_id" INNER JOIN (SELECT * FROM "name" WHERE "gender" = 'm') AS "t55314" ON "t55309"."person_id" = "t55314"."id" INNER JOIN "title" AS "title5444" ON "t55313"."movie_id" = "title5444"."id" AND "movie_info_idx2291"."movie_id" = "title5444"."id" AND "t55309"."movie_id" = "title5444"."id" AND "movie_keyword2974"."movie_id" = "title5444"."id"