SELECT MIN("t75125"."info") AS "movie_budget", MIN("movie_info_idx3180"."info") AS "movie_votes", MIN("t75126"."name") AS "writer", MIN("t75127"."title") AS "complete_gore_movie" FROM "complete_cast" AS "complete_cast1284" INNER JOIN (SELECT * FROM "comp_cast_type" WHERE "kind" IN ('cast', 'crew')) AS "t75119" ON "complete_cast1284"."subject_id" = "t75119"."id" INNER JOIN (SELECT * FROM "comp_cast_type" WHERE "kind" = 'complete+verified') AS "t75120" ON "complete_cast1284"."status_id" = "t75120"."id" INNER JOIN (SELECT * FROM "cast_info" WHERE "note" IN ('(head writer)', '(story editor)', '(story)', '(writer)', '(written by)')) AS "t75121" ON "complete_cast1284"."movie_id" = "t75121"."movie_id" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'genres') AS "t75122" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'votes') AS "t75123" CROSS JOIN (SELECT * FROM "keyword" WHERE "keyword" IN ('blood', 'death', 'female-nudity', 'gore', 'hospital', 'murder', 'violence')) AS "t75124" INNER JOIN (SELECT * FROM "movie_info" WHERE "info" IN ('Horror', 'Thriller')) AS "t75125" ON "t75121"."movie_id" = "t75125"."movie_id" AND "complete_cast1284"."movie_id" = "t75125"."movie_id" AND "t75122"."id" = "t75125"."info_type_id" INNER JOIN "movie_info_idx" AS "movie_info_idx3180" ON "t75121"."movie_id" = "movie_info_idx3180"."movie_id" AND "t75125"."movie_id" = "movie_info_idx3180"."movie_id" AND "complete_cast1284"."movie_id" = "movie_info_idx3180"."movie_id" AND "t75123"."id" = "movie_info_idx3180"."info_type_id" INNER JOIN "movie_keyword" AS "movie_keyword4169" ON "t75121"."movie_id" = "movie_keyword4169"."movie_id" AND "t75125"."movie_id" = "movie_keyword4169"."movie_id" AND "movie_info_idx3180"."movie_id" = "movie_keyword4169"."movie_id" AND "complete_cast1284"."movie_id" = "movie_keyword4169"."movie_id" AND "t75124"."id" = "movie_keyword4169"."keyword_id" INNER JOIN (SELECT * FROM "name" WHERE "gender" = 'm') AS "t75126" ON "t75121"."person_id" = "t75126"."id" INNER JOIN (SELECT * FROM "title" WHERE "production_year" > 2000 AND ("title" LIKE '%Freddy%' OR "title" LIKE '%Jason%' OR "title" LIKE 'Saw%')) AS "t75127" ON "t75125"."movie_id" = "t75127"."id" AND "movie_info_idx3180"."movie_id" = "t75127"."id" AND "t75121"."movie_id" = "t75127"."id" AND "movie_keyword4169"."movie_id" = "t75127"."id" AND "complete_cast1284"."movie_id" = "t75127"."id"