SELECT MIN("t26329"."info") AS "movie_budget", MIN("movie_info_idx1275"."info") AS "movie_votes", MIN("t26330"."name") AS "writer", MIN("t26331"."title") AS "violent_liongate_movie" FROM (SELECT * FROM "cast_info" WHERE "note" IN ('(head writer)', '(story editor)', '(story)', '(writer)', '(written by)')) AS "t26323" CROSS JOIN (SELECT * FROM "company_name" WHERE "name" LIKE 'Lionsgate%') AS "t26324" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'genres') AS "t26325" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'votes') AS "t26326" CROSS JOIN (SELECT * FROM "keyword" WHERE "keyword" IN ('blood', 'death', 'female-nudity', 'gore', 'hospital', 'murder', 'violence')) AS "t26327" INNER JOIN (SELECT * FROM "movie_companies" WHERE "note" LIKE '%(Blu-ray)%') AS "t26328" ON "t26323"."movie_id" = "t26328"."movie_id" AND "t26324"."id" = "t26328"."company_id" INNER JOIN (SELECT * FROM "movie_info" WHERE "info" IN ('Horror', 'Thriller')) AS "t26329" ON "t26323"."movie_id" = "t26329"."movie_id" AND "t26328"."movie_id" = "t26329"."movie_id" AND "t26325"."id" = "t26329"."info_type_id" INNER JOIN "movie_info_idx" AS "movie_info_idx1275" ON "t26323"."movie_id" = "movie_info_idx1275"."movie_id" AND "t26329"."movie_id" = "movie_info_idx1275"."movie_id" AND "t26328"."movie_id" = "movie_info_idx1275"."movie_id" AND "t26326"."id" = "movie_info_idx1275"."info_type_id" INNER JOIN "movie_keyword" AS "movie_keyword1485" ON "t26323"."movie_id" = "movie_keyword1485"."movie_id" AND "t26329"."movie_id" = "movie_keyword1485"."movie_id" AND "movie_info_idx1275"."movie_id" = "movie_keyword1485"."movie_id" AND "t26328"."movie_id" = "movie_keyword1485"."movie_id" AND "t26327"."id" = "movie_keyword1485"."keyword_id" INNER JOIN (SELECT * FROM "name" WHERE "gender" = 'm') AS "t26330" ON "t26323"."person_id" = "t26330"."id" INNER JOIN (SELECT * FROM "title" WHERE "production_year" > 2000 AND ("title" LIKE '%Freddy%' OR "title" LIKE '%Jason%' OR "title" LIKE 'Saw%')) AS "t26331" ON "t26329"."movie_id" = "t26331"."id" AND "movie_info_idx1275"."movie_id" = "t26331"."id" AND "t26323"."movie_id" = "t26331"."id" AND "movie_keyword1485"."movie_id" = "t26331"."id" AND "t26328"."movie_id" = "t26331"."id"