SELECT MIN("t52381"."info") AS "movie_budget", MIN("movie_info_idx2236"."info") AS "movie_votes", MIN("name2502"."name") AS "writer", MIN("title5157"."title") AS "violent_liongate_movie" FROM (SELECT * FROM "cast_info" WHERE "note" IN ('(head writer)', '(story editor)', '(story)', '(writer)', '(written by)')) AS "t52376" CROSS JOIN (SELECT * FROM "company_name" WHERE "name" LIKE 'Lionsgate%') AS "t52377" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'genres') AS "t52378" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'votes') AS "t52379" CROSS JOIN (SELECT * FROM "keyword" WHERE "keyword" IN ('blood', 'death', 'female-nudity', 'gore', 'hospital', 'murder', 'violence')) AS "t52380" INNER JOIN "movie_companies" AS "movie_companies3150" ON "t52376"."movie_id" = "movie_companies3150"."movie_id" AND "t52377"."id" = "movie_companies3150"."company_id" INNER JOIN (SELECT * FROM "movie_info" WHERE "info" IN ('Action', 'Crime', 'Horror', 'Sci-Fi', 'Thriller', 'War')) AS "t52381" ON "t52376"."movie_id" = "t52381"."movie_id" AND "movie_companies3150"."movie_id" = "t52381"."movie_id" AND "t52378"."id" = "t52381"."info_type_id" INNER JOIN "movie_info_idx" AS "movie_info_idx2236" ON "t52376"."movie_id" = "movie_info_idx2236"."movie_id" AND "t52381"."movie_id" = "movie_info_idx2236"."movie_id" AND "movie_companies3150"."movie_id" = "movie_info_idx2236"."movie_id" AND "t52379"."id" = "movie_info_idx2236"."info_type_id" INNER JOIN "movie_keyword" AS "movie_keyword2845" ON "t52376"."movie_id" = "movie_keyword2845"."movie_id" AND "t52381"."movie_id" = "movie_keyword2845"."movie_id" AND "movie_info_idx2236"."movie_id" = "movie_keyword2845"."movie_id" AND "movie_companies3150"."movie_id" = "movie_keyword2845"."movie_id" AND "t52380"."id" = "movie_keyword2845"."keyword_id" INNER JOIN "name" AS "name2502" ON "t52376"."person_id" = "name2502"."id" INNER JOIN "title" AS "title5157" ON "t52381"."movie_id" = "title5157"."id" AND "movie_info_idx2236"."movie_id" = "title5157"."id" AND "t52376"."movie_id" = "title5157"."id" AND "movie_keyword2845"."movie_id" = "title5157"."id" AND "movie_companies3150"."movie_id" = "title5157"."id"