SELECT MIN("t53046"."american_movie") AS "american_movie" FROM (SELECT "t53041"."movie_id", "t53043"."movie_id" AS "movie_id0" FROM (SELECT "t53038"."id" AS "id0", "t53040"."movie_id" FROM (SELECT "id" FROM "company_type" WHERE "kind" = 'production companies' GROUP BY "id") AS "t53037" CROSS JOIN (SELECT "id" FROM "info_type" GROUP BY "id") AS "t53038" INNER JOIN (SELECT "movie_id", "company_type_id" FROM "movie_companies" WHERE "note" LIKE '%(USA)%' AND "note" NOT LIKE '%(TV)%' GROUP BY "movie_id", "company_type_id") AS "t53040" ON "t53037"."id" = "t53040"."company_type_id" GROUP BY "t53038"."id", "t53040"."movie_id") AS "t53041" INNER JOIN (SELECT "movie_id", "info_type_id" FROM "movie_info" WHERE "info" IN ('American', 'Denish', 'Denmark', 'German', 'Germany', 'Norway', 'Norwegian', 'Sweden', 'Swedish', 'USA') GROUP BY "movie_id", "info_type_id") AS "t53043" ON "t53041"."movie_id" = "t53043"."movie_id" AND "t53041"."id0" = "t53043"."info_type_id" GROUP BY "t53041"."movie_id", "t53043"."movie_id") AS "t53044" INNER JOIN (SELECT "id", MIN("title") AS "american_movie" FROM "title" WHERE "production_year" > 1990 GROUP BY "id") AS "t53046" ON "t53044"."movie_id0" = "t53046"."id" AND "t53044"."movie_id" = "t53046"."id"