SELECT MIN("t49344"."movie_keyword") AS "movie_keyword", MIN("t49344"."actor_name") AS "actor_name", MIN("t49346"."hero_movie") AS "hero_movie" FROM (SELECT "t49341"."movie_id", "t49341"."movie_id0", MIN("t49341"."movie_keyword") AS "movie_keyword", MIN("t49342"."actor_name") AS "actor_name" FROM (SELECT "t49336"."person_id", "t49336"."movie_id", "t49339"."movie_id" AS "movie_id0", MIN("t49338"."movie_keyword") AS "movie_keyword" FROM (SELECT "person_id", "movie_id" FROM "cast_info" GROUP BY "person_id", "movie_id") AS "t49336", (SELECT "id", MIN("keyword") AS "movie_keyword" FROM "keyword" WHERE "keyword" IN ('based-on-comic', 'fight', 'marvel-comics', 'second-part', 'sequel', 'superhero', 'tv-special', 'violence') GROUP BY "id") AS "t49338", (SELECT "movie_id", "keyword_id" FROM "movie_keyword" GROUP BY "movie_id", "keyword_id") AS "t49339" WHERE "t49338"."id" = "t49339"."keyword_id" AND "t49336"."movie_id" = "t49339"."movie_id" GROUP BY "t49336"."person_id", "t49336"."movie_id", "t49339"."movie_id") AS "t49341", (SELECT "id", MIN("name") AS "actor_name" FROM "name" GROUP BY "id") AS "t49342" WHERE "t49342"."id" = "t49341"."person_id" GROUP BY "t49341"."movie_id", "t49341"."movie_id0") AS "t49344", (SELECT "id", MIN("title") AS "hero_movie" FROM "title" WHERE "production_year" > 2000 GROUP BY "id") AS "t49346" WHERE "t49346"."id" = "t49344"."movie_id0" AND "t49346"."id" = "t49344"."movie_id"