SELECT MIN("t42935"."name") AS "cast_member_name", MIN("person_info243"."info") AS "cast_member_info" FROM (SELECT * FROM "aka_name" WHERE "name" LIKE '%a%' OR "name" LIKE 'A%') AS "t42932" INNER JOIN "cast_info" AS "cast_info1915" ON "t42932"."person_id" = "cast_info1915"."person_id" CROSS JOIN (SELECT * FROM "info_type" WHERE "info" = 'mini biography') AS "t42933" CROSS JOIN (SELECT * FROM "link_type" WHERE "link" IN ('featured in', 'features', 'referenced in', 'references')) AS "t42934" INNER JOIN "movie_link" AS "movie_link494" ON "t42934"."id" = "movie_link494"."link_type_id" AND "cast_info1915"."movie_id" = "movie_link494"."linked_movie_id" INNER JOIN (SELECT * FROM "name" WHERE "name_pcode_cf" >= 'A' AND "name_pcode_cf" <= 'F' AND ("gender" = 'm' OR "gender" = 'f' AND "name" LIKE 'A%')) AS "t42935" ON "t42932"."person_id" = "t42935"."id" AND "cast_info1915"."person_id" = "t42935"."id" INNER JOIN "person_info" AS "person_info243" ON "t42935"."id" = "person_info243"."person_id" AND "t42933"."id" = "person_info243"."info_type_id" AND "t42932"."person_id" = "person_info243"."person_id" AND "cast_info1915"."person_id" = "person_info243"."person_id" INNER JOIN (SELECT * FROM "title" WHERE "production_year" >= 1980 AND "production_year" <= 2010) AS "t42936" ON "cast_info1915"."movie_id" = "t42936"."id" AND "movie_link494"."linked_movie_id" = "t42936"."id"