SELECT MIN("aka_name497"."name") AS "writer_pseudo_name", MIN("title2391"."title") AS "movie_title" FROM "aka_name" AS "aka_name497" INNER JOIN "cast_info" AS "cast_info894" ON "aka_name497"."person_id" = "cast_info894"."person_id" CROSS JOIN (SELECT * FROM "company_name" WHERE "country_code" = '[us]') AS "t25557" INNER JOIN "movie_companies" AS "movie_companies1519" ON "t25557"."id" = "movie_companies1519"."company_id" AND "cast_info894"."movie_id" = "movie_companies1519"."movie_id" INNER JOIN "name" AS "name894" ON "aka_name497"."person_id" = "name894"."id" AND "cast_info894"."person_id" = "name894"."id" INNER JOIN (SELECT * FROM "role_type" WHERE "role" = 'writer') AS "t25558" ON "cast_info894"."role_id" = "t25558"."id" INNER JOIN "title" AS "title2391" ON "cast_info894"."movie_id" = "title2391"."id" AND "movie_companies1519"."movie_id" = "title2391"."id"