SELECT MIN("aka_name290"."name") AS "costume_designer_pseudo", MIN("title1782"."title") AS "movie_with_costumes" FROM "aka_name" AS "aka_name290" INNER JOIN "cast_info" AS "cast_info369" ON "aka_name290"."person_id" = "cast_info369"."person_id" CROSS JOIN (SELECT * FROM "company_name" WHERE "country_code" = '[us]') AS "t19122" INNER JOIN "movie_companies" AS "movie_companies1304" ON "t19122"."id" = "movie_companies1304"."company_id" AND "cast_info369"."movie_id" = "movie_companies1304"."movie_id" INNER JOIN "name" AS "name369" ON "aka_name290"."person_id" = "name369"."id" AND "cast_info369"."person_id" = "name369"."id" INNER JOIN (SELECT * FROM "role_type" WHERE "role" = 'costume designer') AS "t19123" ON "cast_info369"."role_id" = "t19123"."id" INNER JOIN "title" AS "title1782" ON "cast_info369"."movie_id" = "title1782"."id" AND "movie_companies1304"."movie_id" = "title1782"."id"