SELECT MIN(t.name) AS from_company, MIN(movie_companies.note) AS production_note, MIN(t2.title) AS movie_based_on_book FROM (SELECT * FROM company_name WHERE country_code <> '[pl]') AS t CROSS JOIN (SELECT * FROM company_type WHERE kind <> 'production companies') AS t0 CROSS JOIN (SELECT * FROM keyword WHERE keyword IN ('based-on-novel', 'revenge', 'sequel')) AS t1 CROSS JOIN link_type INNER JOIN movie_companies ON t0.id = movie_companies.company_type_id AND t.id = movie_companies.company_id INNER JOIN movie_keyword ON t1.id = movie_keyword.keyword_id AND movie_companies.movie_id = movie_keyword.movie_id INNER JOIN movie_link ON link_type.id = movie_link.link_type_id AND movie_keyword.movie_id = movie_link.movie_id AND movie_companies.movie_id = movie_link.movie_id INNER JOIN (SELECT * FROM title WHERE production_year > 1950) AS t2 ON movie_link.movie_id = t2.id AND movie_keyword.movie_id = t2.id AND movie_companies.movie_id = t2.id