SELECT MIN(t.name) AS producing_company, MIN(movie_info_idx.info) AS rating, MIN(t4.title) AS movie_about_winning FROM (SELECT * FROM company_name WHERE country_code = '[us]') AS t CROSS JOIN (SELECT * FROM company_type WHERE kind = 'production companies') AS t0 CROSS JOIN (SELECT * FROM info_type WHERE info = 'rating') AS t1 CROSS JOIN (SELECT * FROM info_type WHERE info = 'release dates') AS t2 CROSS JOIN (SELECT * FROM kind_type WHERE kind = 'movie') AS t3 INNER JOIN movie_companies ON t.id = movie_companies.company_id AND t0.id = movie_companies.company_type_id INNER JOIN movie_info ON t2.id = movie_info.info_type_id AND movie_companies.movie_id = movie_info.movie_id INNER JOIN movie_info_idx ON t1.id = movie_info_idx.info_type_id AND movie_info.movie_id = movie_info_idx.movie_id AND movie_companies.movie_id = movie_info_idx.movie_id INNER JOIN (SELECT * FROM title WHERE title <> '' AND (title LIKE 'Champion%' OR title LIKE 'Loser%')) AS t4 ON movie_info.movie_id = t4.id AND t3.id = t4.kind_id AND movie_companies.movie_id = t4.id AND movie_info_idx.movie_id = t4.id