SELECT MIN(t4.info) AS rating, MIN(t5.title) AS western_dark_production FROM (SELECT * FROM info_type WHERE info = 'countries') AS t CROSS JOIN (SELECT * FROM info_type WHERE info = 'rating') AS t0 CROSS JOIN (SELECT * FROM keyword WHERE keyword IN ('murder', 'murder-in-title')) AS t1 CROSS JOIN (SELECT * FROM kind_type WHERE kind = 'movie') AS t2 INNER JOIN (SELECT * FROM movie_info WHERE info IN ('American', 'Denish', 'Denmark', 'German', 'Germany', 'Norway', 'Norwegian', 'Sweden', 'Swedish', 'USA')) AS t3 ON t.id = t3.info_type_id INNER JOIN (SELECT * FROM movie_info_idx WHERE info > '6.0') AS t4 ON t3.movie_id = t4.movie_id AND t0.id = t4.info_type_id INNER JOIN movie_keyword ON t3.movie_id = movie_keyword.movie_id AND t4.movie_id = movie_keyword.movie_id AND t1.id = movie_keyword.keyword_id INNER JOIN (SELECT * FROM title WHERE production_year > 2010 AND (title LIKE '%murder%' OR title LIKE '%Murder%' OR title LIKE '%Mord%')) AS t5 ON t2.id = t5.kind_id AND t3.movie_id = t5.id AND movie_keyword.movie_id = t5.id AND t4.movie_id = t5.id