SELECT MIN(aka_name.name) AS cool_actor_pseudonym, MIN(t1.title) AS series_named_after_char FROM aka_name INNER JOIN cast_info ON aka_name.person_id = cast_info.person_id CROSS JOIN (SELECT * FROM company_name WHERE country_code = '[us]') AS t CROSS JOIN (SELECT * FROM keyword WHERE keyword = 'character-name-in-title') AS t0 INNER JOIN movie_companies ON t.id = movie_companies.company_id AND cast_info.movie_id = movie_companies.movie_id INNER JOIN movie_keyword ON t0.id = movie_keyword.keyword_id AND cast_info.movie_id = movie_keyword.movie_id AND movie_companies.movie_id = movie_keyword.movie_id INNER JOIN name ON aka_name.person_id = name.id AND cast_info.person_id = name.id INNER JOIN (SELECT * FROM title WHERE episode_nr >= 5 AND episode_nr < 100) AS t1 ON cast_info.movie_id = t1.id AND movie_keyword.movie_id = t1.id AND movie_companies.movie_id = t1.id