SELECT MIN(t1.name) AS member_in_charnamed_american_movie, MIN(t1.name) AS a1 FROM cast_info CROSS JOIN (SELECT * FROM company_name WHERE country_code = '[us]') AS t CROSS JOIN (SELECT * FROM keyword WHERE keyword = 'character-name-in-title') AS t0 INNER JOIN movie_companies ON t.id = movie_companies.company_id AND cast_info.movie_id = movie_companies.movie_id INNER JOIN movie_keyword ON t0.id = movie_keyword.keyword_id AND cast_info.movie_id = movie_keyword.movie_id AND movie_companies.movie_id = movie_keyword.movie_id INNER JOIN (SELECT * FROM name WHERE name LIKE 'B%') AS t1 ON cast_info.person_id = t1.id INNER JOIN title ON cast_info.movie_id = title.id AND movie_keyword.movie_id = title.id AND movie_companies.movie_id = title.id