SELECT MIN(t0.name) AS member_in_charnamed_movie, MIN(t0.name) AS a1 FROM cast_info CROSS JOIN company_name CROSS JOIN (SELECT * FROM keyword WHERE keyword = 'character-name-in-title') AS t INNER JOIN movie_companies ON company_name.id = movie_companies.company_id AND cast_info.movie_id = movie_companies.movie_id INNER JOIN movie_keyword ON t.id = movie_keyword.keyword_id AND cast_info.movie_id = movie_keyword.movie_id AND movie_companies.movie_id = movie_keyword.movie_id INNER JOIN (SELECT * FROM name WHERE name LIKE 'X%') AS t0 ON cast_info.person_id = t0.id INNER JOIN title ON cast_info.movie_id = title.id AND movie_keyword.movie_id = title.id AND movie_companies.movie_id = title.id