SELECT MIN(movie_info.info) AS movie_budget, MIN(movie_info_idx.info) AS movie_votes, MIN(title.title) AS movie_title FROM (SELECT * FROM cast_info WHERE note IN ('(executive producer)', '(producer)')) AS t CROSS JOIN (SELECT * FROM info_type WHERE info = 'budget') AS t0 CROSS JOIN (SELECT * FROM info_type WHERE info = 'votes') AS t1 INNER JOIN movie_info ON t.movie_id = movie_info.movie_id AND t0.id = movie_info.info_type_id INNER JOIN movie_info_idx ON t.movie_id = movie_info_idx.movie_id AND movie_info.movie_id = movie_info_idx.movie_id AND t1.id = movie_info_idx.info_type_id INNER JOIN (SELECT * FROM name WHERE gender = 'm' AND name LIKE '%Tim%') AS t2 ON t.person_id = t2.id INNER JOIN title ON movie_info.movie_id = title.id AND movie_info_idx.movie_id = title.id AND t.movie_id = title.id