SELECT MIN(t2.info) AS movie_budget, MIN(movie_info_idx.info) AS movie_votes, MIN(title.title) AS movie_title FROM (SELECT * FROM cast_info WHERE note IN ('(head writer)', '(story editor)', '(story)', '(writer)', '(written by)')) AS t CROSS JOIN (SELECT * FROM info_type WHERE info = 'genres') AS t0 CROSS JOIN (SELECT * FROM info_type WHERE info = 'votes') AS t1 INNER JOIN (SELECT * FROM movie_info WHERE info IN ('Action', 'Crime', 'Horror', 'Sci-Fi', 'Thriller', 'War')) AS t2 ON t.movie_id = t2.movie_id AND t0.id = t2.info_type_id INNER JOIN movie_info_idx ON t.movie_id = movie_info_idx.movie_id AND t2.movie_id = movie_info_idx.movie_id AND t1.id = movie_info_idx.info_type_id INNER JOIN (SELECT * FROM name WHERE gender = 'm') AS t3 ON t.person_id = t3.id INNER JOIN title ON t2.movie_id = title.id AND movie_info_idx.movie_id = title.id AND t.movie_id = title.id