SELECT MIN(t3.name) AS voicing_actress, MIN(t5.title) AS jap_engl_voiced_movie FROM aka_name CROSS JOIN char_name INNER JOIN (SELECT * FROM cast_info WHERE note IN ('(voice)', '(voice) (uncredited)', '(voice: English version)', '(voice: Japanese version)')) AS t ON aka_name.person_id = t.person_id AND char_name.id = t.person_role_id CROSS JOIN (SELECT * FROM company_name WHERE country_code = '[us]') AS t0 CROSS JOIN (SELECT * FROM info_type WHERE info = 'release dates') AS t1 INNER JOIN movie_companies ON t.movie_id = movie_companies.movie_id AND t0.id = movie_companies.company_id INNER JOIN (SELECT * FROM movie_info WHERE info LIKE 'Japan:%200%' OR info LIKE 'USA:%200%') AS t2 ON movie_companies.movie_id = t2.movie_id AND t.movie_id = t2.movie_id AND t1.id = t2.info_type_id INNER JOIN (SELECT * FROM name WHERE gender = 'f' AND name LIKE '%An%') AS t3 ON t.person_id = t3.id AND aka_name.person_id = t3.id INNER JOIN (SELECT * FROM role_type WHERE role = 'actress') AS t4 ON t.role_id = t4.id INNER JOIN (SELECT * FROM title WHERE production_year > 2000) AS t5 ON t2.movie_id = t5.id AND movie_companies.movie_id = t5.id AND t.movie_id = t5.id