SELECT MIN(name.name) AS cast_member, MIN(t4.title) AS complete_dynamic_hero_movie FROM complete_cast INNER JOIN (SELECT * FROM comp_cast_type WHERE kind = 'cast') AS t ON complete_cast.subject_id = t.id INNER JOIN (SELECT * FROM comp_cast_type WHERE kind LIKE '%complete%') AS t0 ON complete_cast.status_id = t0.id CROSS JOIN (SELECT * FROM char_name WHERE name LIKE '%man%' OR name LIKE '%Man%') AS t1 INNER JOIN cast_info ON complete_cast.movie_id = cast_info.movie_id AND t1.id = cast_info.person_role_id CROSS JOIN (SELECT * FROM keyword WHERE keyword IN ('based-on-comic', 'claw', 'fight', 'laser', 'magnet', 'marvel-comics', 'superhero', 'tv-special', 'violence', 'web')) AS t2 CROSS JOIN (SELECT * FROM kind_type WHERE kind = 'movie') AS t3 INNER JOIN movie_keyword ON cast_info.movie_id = movie_keyword.movie_id AND complete_cast.movie_id = movie_keyword.movie_id AND t2.id = movie_keyword.keyword_id INNER JOIN name ON cast_info.person_id = name.id INNER JOIN (SELECT * FROM title WHERE production_year > 2000) AS t4 ON t3.id = t4.kind_id AND movie_keyword.movie_id = t4.id AND cast_info.movie_id = t4.id AND complete_cast.movie_id = t4.id