SELECT MIN(t.name) AS movie_company, MIN(t5.info) AS rating, MIN(t6.title) AS western_violent_movie FROM (SELECT * FROM company_name WHERE country_code <> '[us]') AS t CROSS JOIN company_type CROSS JOIN (SELECT * FROM info_type WHERE info = 'countries') AS t0 CROSS JOIN (SELECT * FROM info_type WHERE info = 'rating') AS t1 CROSS JOIN (SELECT * FROM keyword WHERE keyword IN ('blood', 'murder', 'murder-in-title', 'violence')) AS t2 CROSS JOIN (SELECT * FROM kind_type WHERE kind IN ('episode', 'movie')) AS t3 INNER JOIN movie_companies ON company_type.id = movie_companies.company_type_id AND t.id = movie_companies.company_id INNER JOIN (SELECT * FROM movie_info WHERE info IN ('American', 'Danish', 'Denmark', 'German', 'Germany', 'Norway', 'Norwegian', 'Sweden', 'Swedish', 'USA')) AS t4 ON movie_companies.movie_id = t4.movie_id AND t0.id = t4.info_type_id INNER JOIN (SELECT * FROM movie_info_idx WHERE info < '8.5') AS t5 ON t4.movie_id = t5.movie_id AND movie_companies.movie_id = t5.movie_id AND t1.id = t5.info_type_id INNER JOIN movie_keyword ON t4.movie_id = movie_keyword.movie_id AND t5.movie_id = movie_keyword.movie_id AND movie_companies.movie_id = movie_keyword.movie_id AND t2.id = movie_keyword.keyword_id INNER JOIN (SELECT * FROM title WHERE production_year > 2005) AS t6 ON t3.id = t6.kind_id AND t4.movie_id = t6.id AND movie_keyword.movie_id = t6.id AND t5.movie_id = t6.id AND movie_companies.movie_id = t6.id