SELECT MIN(char_name.name) AS voiced_char_name, MIN(t4.name) AS voicing_actress_name, MIN(t6.title) AS voiced_action_movie_jap_eng FROM aka_name CROSS JOIN char_name INNER JOIN (SELECT * FROM cast_info WHERE note IN ('(voice)', '(voice) (uncredited)', '(voice: English version)', '(voice: Japanese version)')) AS t ON aka_name.person_id = t.person_id AND char_name.id = t.person_role_id CROSS JOIN (SELECT * FROM company_name WHERE country_code = '[us]') AS t0 CROSS JOIN (SELECT * FROM info_type WHERE info = 'release dates') AS t1 CROSS JOIN (SELECT * FROM keyword WHERE keyword IN ('hand-to-hand-combat', 'hero', 'martial-arts')) AS t2 INNER JOIN movie_companies ON t.movie_id = movie_companies.movie_id AND t0.id = movie_companies.company_id INNER JOIN (SELECT * FROM movie_info WHERE info LIKE 'Japan:%201%' OR info LIKE 'USA:%201%') AS t3 ON movie_companies.movie_id = t3.movie_id AND t.movie_id = t3.movie_id AND t1.id = t3.info_type_id INNER JOIN movie_keyword ON movie_companies.movie_id = movie_keyword.movie_id AND t3.movie_id = movie_keyword.movie_id AND t.movie_id = movie_keyword.movie_id AND t2.id = movie_keyword.keyword_id INNER JOIN (SELECT * FROM name WHERE gender = 'f' AND name LIKE '%An%') AS t4 ON t.person_id = t4.id AND aka_name.person_id = t4.id INNER JOIN (SELECT * FROM role_type WHERE role = 'actress') AS t5 ON t.role_id = t5.id INNER JOIN (SELECT * FROM title WHERE production_year > 2010) AS t6 ON t3.movie_id = t6.id AND movie_companies.movie_id = t6.id AND t.movie_id = t6.id AND movie_keyword.movie_id = t6.id