SELECT MIN(t1.name) AS movie_company, MIN(t8.info) AS rating, MIN(t9.title) AS complete_euro_dark_movie FROM complete_cast INNER JOIN (SELECT * FROM comp_cast_type WHERE kind = 'cast') AS t ON complete_cast.subject_id = t.id INNER JOIN (SELECT * FROM comp_cast_type WHERE kind = 'complete') AS t0 ON complete_cast.status_id = t0.id CROSS JOIN (SELECT * FROM company_name WHERE country_code <> '[us]') AS t1 CROSS JOIN company_type CROSS JOIN (SELECT * FROM info_type WHERE info = 'countries') AS t2 CROSS JOIN (SELECT * FROM info_type WHERE info = 'rating') AS t3 CROSS JOIN (SELECT * FROM keyword WHERE keyword IN ('blood', 'murder', 'murder-in-title', 'violence')) AS t4 CROSS JOIN (SELECT * FROM kind_type WHERE kind IN ('episode', 'movie')) AS t5 INNER JOIN (SELECT * FROM movie_companies WHERE note LIKE '%(200%)%' AND note NOT LIKE '%(USA)%') AS t6 ON complete_cast.movie_id = t6.movie_id AND company_type.id = t6.company_type_id AND t1.id = t6.company_id INNER JOIN (SELECT * FROM movie_info WHERE info IN ('American', 'Danish', 'Denmark', 'German', 'Germany', 'Norway', 'Norwegian', 'Sweden', 'Swedish', 'USA')) AS t7 ON t6.movie_id = t7.movie_id AND complete_cast.movie_id = t7.movie_id AND t2.id = t7.info_type_id INNER JOIN (SELECT * FROM movie_info_idx WHERE info < '8.5') AS t8 ON t7.movie_id = t8.movie_id AND t6.movie_id = t8.movie_id AND complete_cast.movie_id = t8.movie_id AND t3.id = t8.info_type_id INNER JOIN movie_keyword ON t7.movie_id = movie_keyword.movie_id AND t8.movie_id = movie_keyword.movie_id AND t6.movie_id = movie_keyword.movie_id AND complete_cast.movie_id = movie_keyword.movie_id AND t4.id = movie_keyword.keyword_id INNER JOIN (SELECT * FROM title WHERE production_year > 2005) AS t9 ON t5.id = t9.kind_id AND (t7.movie_id = t9.id AND movie_keyword.movie_id = t9.id) AND (t8.movie_id = t9.id AND (t6.movie_id = t9.id AND complete_cast.movie_id = t9.id))