SELECT MIN(t1.name) AS voiced_char, MIN(t8.name) AS voicing_actress, MIN(t10.title) AS voiced_animation FROM aka_name CROSS JOIN complete_cast INNER JOIN (SELECT * FROM comp_cast_type WHERE kind = 'cast') AS t ON complete_cast.subject_id = t.id INNER JOIN (SELECT * FROM comp_cast_type WHERE kind = 'complete+verified') AS t0 ON complete_cast.status_id = t0.id CROSS JOIN (SELECT * FROM char_name WHERE name = 'Queen') AS t1 INNER JOIN (SELECT * FROM cast_info WHERE note IN ('(voice)', '(voice) (uncredited)', '(voice: English version)')) AS t2 ON complete_cast.movie_id = t2.movie_id AND aka_name.person_id = t2.person_id AND t1.id = t2.person_role_id CROSS JOIN (SELECT * FROM company_name WHERE country_code = '[us]') AS t3 CROSS JOIN (SELECT * FROM info_type WHERE info = 'release dates') AS t4 CROSS JOIN (SELECT * FROM info_type WHERE info = 'height') AS t5 CROSS JOIN (SELECT * FROM keyword WHERE keyword = 'computer-animation') AS t6 INNER JOIN movie_companies ON t2.movie_id = movie_companies.movie_id AND complete_cast.movie_id = movie_companies.movie_id AND t3.id = movie_companies.company_id INNER JOIN (SELECT * FROM movie_info WHERE info LIKE 'USA:%200%') AS t7 ON movie_companies.movie_id = t7.movie_id AND t2.movie_id = t7.movie_id AND complete_cast.movie_id = t7.movie_id AND t4.id = t7.info_type_id INNER JOIN movie_keyword ON movie_companies.movie_id = movie_keyword.movie_id AND t7.movie_id = movie_keyword.movie_id AND t2.movie_id = movie_keyword.movie_id AND complete_cast.movie_id = movie_keyword.movie_id AND t6.id = movie_keyword.keyword_id INNER JOIN (SELECT * FROM name WHERE gender = 'f' AND name LIKE '%An%') AS t8 ON t2.person_id = t8.id AND aka_name.person_id = t8.id INNER JOIN person_info ON t8.id = person_info.person_id AND t2.person_id = person_info.person_id AND t5.id = person_info.info_type_id INNER JOIN (SELECT * FROM role_type WHERE role = 'actress') AS t9 ON t2.role_id = t9.id INNER JOIN (SELECT * FROM title WHERE title = 'Shrek 2' AND (production_year >= 2000 AND production_year <= 2005)) AS t10 ON t7.movie_id = t10.id AND movie_companies.movie_id = t10.id AND t2.movie_id = t10.id AND movie_keyword.movie_id = t10.id AND complete_cast.movie_id = t10.id