SELECT MIN(title.title) AS movie_title FROM (SELECT * FROM company_name WHERE country_code = '[nl]') AS t CROSS JOIN (SELECT * FROM keyword WHERE keyword = 'character-name-in-title') AS t0 INNER JOIN movie_companies ON t.id = movie_companies.company_id INNER JOIN movie_keyword ON t0.id = movie_keyword.keyword_id AND movie_companies.movie_id = movie_keyword.movie_id INNER JOIN title ON movie_companies.movie_id = title.id AND movie_keyword.movie_id = title.id