SELECT MIN(t5.info) AS movie_budget, MIN(movie_info_idx.info) AS movie_votes, MIN(t6.name) AS writer, MIN(t7.title) AS complete_gore_movie FROM complete_cast INNER JOIN (SELECT * FROM comp_cast_type WHERE kind IN ('cast', 'crew')) AS t ON complete_cast.subject_id = t.id INNER JOIN (SELECT * FROM comp_cast_type WHERE kind = 'complete+verified') AS t0 ON complete_cast.status_id = t0.id INNER JOIN (SELECT * FROM cast_info WHERE note IN ('(head writer)', '(story editor)', '(story)', '(writer)', '(written by)')) AS t1 ON complete_cast.movie_id = t1.movie_id CROSS JOIN (SELECT * FROM info_type WHERE info = 'genres') AS t2 CROSS JOIN (SELECT * FROM info_type WHERE info = 'votes') AS t3 CROSS JOIN (SELECT * FROM keyword WHERE keyword IN ('blood', 'death', 'female-nudity', 'gore', 'hospital', 'murder', 'violence')) AS t4 INNER JOIN (SELECT * FROM movie_info WHERE info IN ('Horror', 'Thriller')) AS t5 ON t1.movie_id = t5.movie_id AND complete_cast.movie_id = t5.movie_id AND t2.id = t5.info_type_id INNER JOIN movie_info_idx ON t1.movie_id = movie_info_idx.movie_id AND t5.movie_id = movie_info_idx.movie_id AND complete_cast.movie_id = movie_info_idx.movie_id AND t3.id = movie_info_idx.info_type_id INNER JOIN movie_keyword ON t1.movie_id = movie_keyword.movie_id AND t5.movie_id = movie_keyword.movie_id AND movie_info_idx.movie_id = movie_keyword.movie_id AND complete_cast.movie_id = movie_keyword.movie_id AND t4.id = movie_keyword.keyword_id INNER JOIN (SELECT * FROM name WHERE gender = 'm') AS t6 ON t1.person_id = t6.id INNER JOIN (SELECT * FROM title WHERE production_year > 2000 AND (title LIKE '%Freddy%' OR title LIKE '%Jason%' OR title LIKE 'Saw%')) AS t7 ON t5.movie_id = t7.id AND movie_info_idx.movie_id = t7.id AND t1.movie_id = t7.id AND movie_keyword.movie_id = t7.id AND complete_cast.movie_id = t7.id