SELECT MIN(t2.name) AS of_person, MIN(t4.title) AS biography_movie FROM (SELECT * FROM aka_name WHERE name LIKE '%a%') AS t INNER JOIN cast_info ON t.person_id = cast_info.person_id CROSS JOIN (SELECT * FROM info_type WHERE info = 'mini biography') AS t0 CROSS JOIN (SELECT * FROM link_type WHERE link = 'features') AS t1 INNER JOIN movie_link ON t1.id = movie_link.link_type_id AND cast_info.movie_id = movie_link.linked_movie_id INNER JOIN (SELECT * FROM name WHERE name_pcode_cf >= 'A' AND name_pcode_cf <= 'F' AND (gender = 'm' OR gender = 'f' AND name LIKE 'B%')) AS t2 ON t.person_id = t2.id AND cast_info.person_id = t2.id INNER JOIN (SELECT * FROM person_info WHERE note = 'Volker Boehm') AS t3 ON t2.id = t3.person_id AND t0.id = t3.info_type_id AND t.person_id = t3.person_id AND cast_info.person_id = t3.person_id INNER JOIN (SELECT * FROM title WHERE production_year >= 1980 AND production_year <= 1995) AS t4 ON cast_info.movie_id = t4.id AND movie_link.linked_movie_id = t4.id