SELECT MIN(t2.name) AS cast_member_name, MIN(person_info.info) AS cast_member_info FROM (SELECT * FROM aka_name WHERE name LIKE '%a%' OR name LIKE 'A%') AS t INNER JOIN cast_info ON t.person_id = cast_info.person_id CROSS JOIN (SELECT * FROM info_type WHERE info = 'mini biography') AS t0 CROSS JOIN (SELECT * FROM link_type WHERE link IN ('featured in', 'features', 'referenced in', 'references')) AS t1 INNER JOIN movie_link ON t1.id = movie_link.link_type_id AND cast_info.movie_id = movie_link.linked_movie_id INNER JOIN (SELECT * FROM name WHERE name_pcode_cf >= 'A' AND name_pcode_cf <= 'F' AND (gender = 'm' OR gender = 'f' AND name LIKE 'A%')) AS t2 ON t.person_id = t2.id AND cast_info.person_id = t2.id INNER JOIN person_info ON t2.id = person_info.person_id AND t0.id = person_info.info_type_id AND t.person_id = person_info.person_id AND cast_info.person_id = person_info.person_id INNER JOIN (SELECT * FROM title WHERE production_year >= 1980 AND production_year <= 2010) AS t3 ON cast_info.movie_id = t3.id AND movie_link.linked_movie_id = t3.id