SELECT MIN(aka_name.name) AS acress_pseudonym, MIN(t4.title) AS japanese_anime_movie FROM aka_name INNER JOIN (SELECT * FROM cast_info WHERE note = '(voice: English version)') AS t ON aka_name.person_id = t.person_id CROSS JOIN (SELECT * FROM company_name WHERE country_code = '[jp]') AS t0 INNER JOIN (SELECT * FROM movie_companies WHERE note LIKE '%(Japan)%' AND (note LIKE '%(2006)%' OR note LIKE '%(2007)%') AND note NOT LIKE '%(USA)%') AS t1 ON t0.id = t1.company_id AND t.movie_id = t1.movie_id INNER JOIN (SELECT * FROM name WHERE name LIKE '%Yo%' AND name NOT LIKE '%Yu%') AS t2 ON aka_name.person_id = t2.id AND t.person_id = t2.id INNER JOIN (SELECT * FROM role_type WHERE role = 'actress') AS t3 ON t.role_id = t3.id INNER JOIN (SELECT * FROM title WHERE production_year >= 2006 AND production_year <= 2007 AND (title LIKE 'One Piece%' OR title LIKE 'Dragon Ball Z%')) AS t4 ON t.movie_id = t4.id AND t1.movie_id = t4.id