SELECT MIN(aka_name.name) AS costume_designer_pseudo, MIN(title.title) AS movie_with_costumes FROM aka_name INNER JOIN cast_info ON aka_name.person_id = cast_info.person_id CROSS JOIN (SELECT * FROM company_name WHERE country_code = '[us]') AS t INNER JOIN movie_companies ON t.id = movie_companies.company_id AND cast_info.movie_id = movie_companies.movie_id INNER JOIN name ON aka_name.person_id = name.id AND cast_info.person_id = name.id INNER JOIN (SELECT * FROM role_type WHERE role = 'costume designer') AS t0 ON cast_info.role_id = t0.id INNER JOIN title ON cast_info.movie_id = title.id AND movie_companies.movie_id = title.id