SELECT MIN(aka_name.name) AS alternative_name, MIN(char_name.name) AS voiced_char_name, MIN(t1.name) AS voicing_actress, MIN(title.title) AS american_movie FROM aka_name CROSS JOIN char_name INNER JOIN (SELECT * FROM cast_info WHERE note IN ('(voice)', '(voice) (uncredited)', '(voice: English version)', '(voice: Japanese version)')) AS t ON char_name.id = t.person_role_id AND aka_name.person_id = t.person_id CROSS JOIN (SELECT * FROM company_name WHERE country_code = '[us]') AS t0 INNER JOIN movie_companies ON t.movie_id = movie_companies.movie_id AND t0.id = movie_companies.company_id INNER JOIN (SELECT * FROM name WHERE gender = 'f') AS t1 ON t.person_id = t1.id AND aka_name.person_id = t1.id INNER JOIN (SELECT * FROM role_type WHERE role = 'actress') AS t2 ON t.role_id = t2.id INNER JOIN title ON t.movie_id = title.id AND movie_companies.movie_id = title.id