SELECT item.i_item_id, item.i_item_desc, store.s_state, COUNT(*) AS store_sales_quantitycount, AVG(store_sales.ss_quantity) AS store_sales_quantityave, STDDEV_SAMP(store_sales.ss_quantity) AS store_sales_quantitystdev, STDDEV_SAMP(store_sales.ss_quantity) / AVG(store_sales.ss_quantity) AS store_sales_quantitycov, COUNT(*) AS store_returns_quantitycount, AVG(store_returns.sr_return_quantity) AS store_returns_quantityave, STDDEV_SAMP(store_returns.sr_return_quantity) AS store_returns_quantitystdev, STDDEV_SAMP(store_returns.sr_return_quantity) / AVG(store_returns.sr_return_quantity) AS store_returns_quantitycov, COUNT(*) AS catalog_sales_quantitycount, AVG(catalog_sales.cs_quantity) AS catalog_sales_quantityave, STDDEV_SAMP(catalog_sales.cs_quantity) AS catalog_sales_quantitystdev, STDDEV_SAMP(catalog_sales.cs_quantity) / AVG(catalog_sales.cs_quantity) AS catalog_sales_quantitycov FROM store_sales, store_returns, catalog_sales, date_dim, date_dim AS date_dim0, date_dim AS date_dim1, store, item WHERE date_dim.d_quarter_name = '2001Q1' AND (date_dim.d_date_sk = store_sales.ss_sold_date_sk AND item.i_item_sk = store_sales.ss_item_sk) AND (store.s_store_sk = store_sales.ss_store_sk AND (store_sales.ss_customer_sk = store_returns.sr_customer_sk AND store_sales.ss_item_sk = store_returns.sr_item_sk)) AND (store_sales.ss_ticket_number = store_returns.sr_ticket_number AND (store_returns.sr_returned_date_sk = date_dim0.d_date_sk AND (date_dim0.d_quarter_name = '2001Q1' OR date_dim0.d_quarter_name = '2001Q2' OR date_dim0.d_quarter_name = '2001Q3')) AND (store_returns.sr_customer_sk = catalog_sales.cs_bill_customer_sk AND store_returns.sr_item_sk = catalog_sales.cs_item_sk AND (catalog_sales.cs_sold_date_sk = date_dim1.d_date_sk AND (date_dim1.d_quarter_name = '2001Q1' OR date_dim1.d_quarter_name = '2001Q2' OR date_dim1.d_quarter_name = '2001Q3')))) GROUP BY item.i_item_id, item.i_item_desc, store.s_state ORDER BY item.i_item_id, item.i_item_desc, store.s_state LIMIT 100