SELECT customer.c_customer_id FROM (SELECT store_returns.sr_customer_sk AS ctr_customer_sk, store_returns.sr_store_sk AS ctr_store_sk, SUM(store_returns.sr_fee) AS ctr_total_return FROM store_returns INNER JOIN (SELECT * FROM date_dim WHERE d_year = 2000) AS t ON store_returns.sr_returned_date_sk = t.d_date_sk GROUP BY store_returns.sr_customer_sk, store_returns.sr_store_sk) AS t1 INNER JOIN (SELECT * FROM store WHERE s_state = 'TN') AS t2 ON t1.ctr_store_sk = t2.s_store_sk INNER JOIN customer ON t1.ctr_customer_sk = customer.c_customer_sk INNER JOIN (SELECT t6.ctr_store_sk, AVG(t6.ctr_total_return) AS f1 FROM (SELECT store_returns0.sr_store_sk AS ctr_store_sk, SUM(store_returns0.sr_fee) AS ctr_total_return FROM store_returns AS store_returns0 INNER JOIN (SELECT * FROM date_dim WHERE d_year = 2000) AS t3 ON store_returns0.sr_returned_date_sk = t3.d_date_sk GROUP BY store_returns0.sr_customer_sk, store_returns0.sr_store_sk) AS t6 GROUP BY t6.ctr_store_sk) AS t7 ON t1.ctr_store_sk = t7.ctr_store_sk AND t1.ctr_total_return > t7.f1 * 1.2 ORDER BY customer.c_customer_id LIMIT 100