SELECT item.i_product_name, item.i_brand, item.i_class, item.i_category, AVG(inventory.inv_quantity_on_hand) AS qoh FROM inventory, date_dim, item WHERE inventory.inv_date_sk = date_dim.d_date_sk AND inventory.inv_item_sk = item.i_item_sk AND date_dim.d_month_seq >= 1200 AND date_dim.d_month_seq <= 1200 + 11 GROUP BY ROLLUP(item.i_brand, item.i_class, item.i_category, item.i_product_name) ORDER BY AVG(inventory.inv_quantity_on_hand), item.i_product_name, item.i_brand, item.i_class, item.i_category LIMIT 100