ustomer_sk INNER JOIN customer_address AS customer_address0 ON customer0.c_current_addr_sk = customer_address0.ca_address_sk AND customer0.c_birth_country <> UPPER(customer_address0.ca_country) AND t4.s_zip = customer_address0.ca_zip GROUP BY t4.s_store_name, t4.s_state, item0.i_current_price, item0.i_size, item0.i_color, item0.i_units, item0.i_manager_id, customer0.c_first_name, customer0.c_last_name, customer_address0.ca_state) AS t5) AS t7 ON t3.paid > t7.EXPR0 ORDER BY t3.c_last_name, t3.c_first_name, t3.s_store_name