SELECT item.i_item_id, item.i_item_desc, store.s_store_id, store.s_store_name, MAX(store_sales.ss_quantity) AS store_sales_quantity, MAX(store_returns.sr_return_quantity) AS store_returns_quantity, MAX(catalog_sales.cs_quantity) AS catalog_sales_quantity FROM store_sales, store_returns, catalog_sales, date_dim, date_dim AS date_dim0, date_dim AS date_dim1, store, item WHERE date_dim.d_moy = 4 AND date_dim.d_year = 1998 AND (date_dim.d_date_sk = store_sales.ss_sold_date_sk AND item.i_item_sk = store_sales.ss_item_sk) AND (store.s_store_sk = store_sales.ss_store_sk AND store_sales.ss_customer_sk = store_returns.sr_customer_sk AND (store_sales.ss_item_sk = store_returns.sr_item_sk AND store_sales.ss_ticket_number = store_returns.sr_ticket_number)) AND (store_returns.sr_returned_date_sk = date_dim0.d_date_sk AND date_dim0.d_moy >= 4 AND (date_dim0.d_moy <= 4 + 3 AND date_dim0.d_year = 1998) AND (store_returns.sr_customer_sk = catalog_sales.cs_bill_customer_sk AND store_returns.sr_item_sk = catalog_sales.cs_item_sk AND (catalog_sales.cs_sold_date_sk = date_dim1.d_date_sk AND (date_dim1.d_year = 1998 OR date_dim1.d_year = 1998 + 1 OR date_dim1.d_year = 1998 + 2)))) GROUP BY item.i_item_id, item.i_item_desc, store.s_store_id, store.s_store_name ORDER BY item.i_item_id, item.i_item_desc, store.s_store_id, store.s_store_name LIMIT 100