SELECT t7.d_week_seq1, ROUND(t7.sun_sales1 / t16.sun_sales2, 2), ROUND(t7.mon_sales1 / t16.mon_sales2, 2), ROUND(t7.tue_sales1 / t16.tue_sales2, 2), ROUND(t7.wed_sales1 / t16.wed_sales2, 2), ROUND(t7.thu_sales1 / t16.thu_sales2, 2), ROUND(t7.fri_sales1 / t16.fri_sales2, 2), ROUND(t7.sat_sales1 / t16.sat_sales2, 2) FROM (SELECT t5.d_week_seq AS d_week_seq1, t5.sun_sales AS sun_sales1, t5.mon_sales AS mon_sales1, t5.tue_sales AS tue_sales1, t5.wed_sales AS wed_sales1, t5.thu_sales AS thu_sales1, t5.fri_sales AS fri_sales1, t5.sat_sales AS sat_sales1 FROM (SELECT date_dim.d_week_seq, SUM(CASE WHEN date_dim.d_day_name = 'Sunday' THEN t2.sales_price ELSE NULL END) AS sun_sales, SUM(CASE WHEN date_dim.d_day_name = 'Monday' THEN t2.sales_price ELSE NULL END) AS mon_sales, SUM(CASE WHEN date_dim.d_day_name = 'Tuesday' THEN t2.sales_price ELSE NULL END) AS tue_sales, SUM(CASE WHEN date_dim.d_day_name = 'Wednesday' THEN t2.sales_price ELSE NULL END) AS wed_sales, SUM(CASE WHEN date_dim.d_day_name = 'Thursday' THEN t2.sales_price ELSE NULL END) AS thu_sales, SUM(CASE WHEN date_dim.d_day_name = 'Friday' THEN t2.sales_price ELSE NULL END) AS fri_sales, SUM(CASE WHEN date_dim.d_day_name = 'Saturday' THEN t2.sales_price ELSE NULL END) AS sat_sales FROM (SELECT * FROM (SELECT ws_sold_date_sk AS sold_date_sk, ws_ext_sales_price AS sales_price FROM web_sales UNION ALL SELECT cs_sold_date_sk AS sold_date_sk, cs_ext_sales_price AS sales_price FROM catalog_sales) AS t1) AS t2, date_dim WHERE date_dim.d_date_sk = t2.sold_date_sk GROUP BY date_dim.d_week_seq) AS t5, date_dim AS date_dim0 WHERE date_dim0.d_week_seq = t5.d_week_seq AND date_dim0.d_year = 1998) AS t7, (SELECT t14.d_week_seq AS d_week_seq2, t14.sun_sales AS sun_sales2, t14.mon_sales AS mon_sales2, t14.tue_sales AS tue_sales2, t14.wed_sales AS wed_sales2, t14.thu_sales AS thu_sales2, t14.fri_sales AS fri_sales2, t14.sat_sales AS sat_sales2 FROM (SELECT date_dim1.d_week_seq, SUM(CASE WHEN date_dim1.d_day_name = 'Sunday' THEN t11.sales_price ELSE NULL END) AS sun_sales, SUM(CASE WHEN date_dim1.d_day_name = 'Monday' THEN t11.sales_price ELSE NULL END) AS mon_sales, SUM(CASE WHEN date_dim1.d_day_name = 'Tuesday' THEN t11.sales_price ELSE NULL END) AS tue_sales, SUM(CASE WHEN date_dim1.d_day_name = 'Wednesday' THEN t11.sales_price ELSE NULL END) AS wed_sales, SUM(CASE WHEN date_dim1.d_day_name = 'Thursday' THEN t11.sales_price ELSE NULL END) AS thu_sales, SUM(CASE WHEN date_dim1.d_day_name = 'Friday' THEN t11.sales_price ELSE NULL END) AS fri_sales, SUM(CASE WHEN date_dim1.d_day_name = 'Saturday' THEN t11.sales_price ELSE NULL END) AS sat_sales FROM (SELECT * FROM (SELECT ws_sold_date_sk AS sold_date_sk, ws_ext_sales_price AS sales_price FROM web_sales UNION ALL SELECT cs_sold_date_sk AS sold_date_sk, cs_ext_sales_price AS sales_price FROM catalog_sales) AS t10) AS t11, date_dim AS date_dim1 WHERE date_dim1.d_date_sk = t11.sold_date_sk GROUP BY date_dim1.d_week_seq) AS t14, date_dim AS date_dim2 WHERE date_dim2.d_week_seq = t14.d_week_seq AND date_dim2.d_year = 1998 + 1) AS t16 WHERE t7.d_week_seq1 = t16.d_week_seq2 - 53 ORDER BY t7.d_week_seq1