SELECT customer.c_customer_id, customer.c_salutation, customer.c_first_name, customer.c_last_name, customer.c_preferred_cust_flag, customer.c_birth_day, customer.c_birth_month, customer.c_birth_year, customer.c_birth_country, customer.c_login, customer.c_email_address, customer.c_last_review_date_sk, t1.ctr_total_return FROM (SELECT web_returns.wr_returning_customer_sk AS ctr_customer_sk, customer_address.ca_state AS ctr_state, SUM(web_returns.wr_return_amt) AS ctr_total_return FROM web_returns INNER JOIN (SELECT * FROM date_dim WHERE d_year = 2000) AS t ON web_returns.wr_returned_date_sk = t.d_date_sk INNER JOIN customer_address ON web_returns.wr_returning_addr_sk = customer_address.ca_address_sk GROUP BY web_returns.wr_returning_customer_sk, customer_address.ca_state) AS t1 CROSS JOIN (SELECT * FROM customer_address WHERE ca_state = 'AR') AS t2 INNER JOIN customer ON t2.ca_address_sk = customer.c_current_addr_sk AND t1.ctr_customer_sk = customer.c_customer_sk INNER JOIN (SELECT t6.ctr_state, AVG(t6.ctr_total_return) AS f1 FROM (SELECT customer_address1.ca_state AS ctr_state, SUM(web_returns0.wr_return_amt) AS ctr_total_return FROM web_returns AS web_returns0 INNER JOIN (SELECT * FROM date_dim WHERE d_year = 2000) AS t3 ON web_returns0.wr_returned_date_sk = t3.d_date_sk INNER JOIN customer_address AS customer_address1 ON web_returns0.wr_returning_addr_sk = customer_address1.ca_address_sk GROUP BY web_returns0.wr_returning_customer_sk, customer_address1.ca_state) AS t6 GROUP BY t6.ctr_state) AS t7 ON t1.ctr_state = t7.ctr_state AND t1.ctr_total_return > t7.f1 * 1.2 ORDER BY customer.c_customer_id, customer.c_salutation, customer.c_first_name, customer.c_last_name, customer.c_preferred_cust_flag, customer.c_birth_day, customer.c_birth_month, customer.c_birth_year, customer.c_birth_country, customer.c_login, customer.c_email_address, customer.c_last_review_date_sk, t1.ctr_total_return LIMIT 100