SELECT SUM(catalog_sales.cs_ext_discount_amt) AS "excess discount amount" FROM catalog_sales INNER JOIN (SELECT * FROM item WHERE i_manufact_id = 722) AS t ON catalog_sales.cs_item_sk = t.i_item_sk INNER JOIN (SELECT * FROM date_dim WHERE d_date >= DATE '2001-03-09' AND d_date <= (DATE '2001-03-09' + INTERVAL '90' DAY)) AS t0 ON catalog_sales.cs_sold_date_sk = t0.d_date_sk INNER JOIN (SELECT catalog_sales0.cs_item_sk, AVG(catalog_sales0.cs_ext_discount_amt) AS f1 FROM catalog_sales AS catalog_sales0 INNER JOIN (SELECT * FROM date_dim WHERE d_date >= DATE '2001-03-09' AND d_date <= (DATE '2001-03-09' + INTERVAL '90' DAY)) AS t1 ON catalog_sales0.cs_sold_date_sk = t1.d_date_sk GROUP BY catalog_sales0.cs_item_sk) AS t3 ON t.i_item_sk = t3.cs_item_sk AND catalog_sales.cs_ext_discount_amt > 1.3 * t3.f1 LIMIT 100