SELECT i_manufact_id, SUM(total_sales) AS total_sales FROM (SELECT item.i_manufact_id, SUM(store_sales.ss_ext_sales_price) AS total_sales FROM store_sales INNER JOIN (SELECT * FROM date_dim WHERE d_year = 2001 AND d_moy = 3) AS t ON store_sales.ss_sold_date_sk = t.d_date_sk INNER JOIN (SELECT * FROM customer_address WHERE ca_gmt_offset = -5) AS t0 ON store_sales.ss_addr_sk = t0.ca_address_sk INNER JOIN item ON store_sales.ss_item_sk = item.i_item_sk INNER JOIN (SELECT i_manufact_id FROM item WHERE i_category = 'Books') AS t2 ON item.i_manufact_id = t2.i_manufact_id GROUP BY item.i_manufact_id UNION ALL SELECT item1.i_manufact_id, SUM(catalog_sales.cs_ext_sales_price) AS total_sales FROM catalog_sales INNER JOIN (SELECT * FROM date_dim WHERE d_year = 2001 AND d_moy = 3) AS t5 ON catalog_sales.cs_sold_date_sk = t5.d_date_sk INNER JOIN (SELECT * FROM customer_address WHERE ca_gmt_offset = -5) AS t6 ON catalog_sales.cs_bill_addr_sk = t6.ca_address_sk INNER JOIN item AS item1 ON catalog_sales.cs_item_sk = item1.i_item_sk INNER JOIN (SELECT i_manufact_id FROM item WHERE i_category = 'Books') AS t8 ON item1.i_manufact_id = t8.i_manufact_id GROUP BY item1.i_manufact_id UNION ALL SELECT item3.i_manufact_id, SUM(web_sales.ws_ext_sales_price) AS total_sales FROM web_sales INNER JOIN (SELECT * FROM date_dim WHERE d_year = 2001 AND d_moy = 3) AS t11 ON web_sales.ws_sold_date_sk = t11.d_date_sk INNER JOIN (SELECT * FROM customer_address WHERE ca_gmt_offset = -5) AS t12 ON web_sales.ws_bill_addr_sk = t12.ca_address_sk INNER JOIN item AS item3 ON web_sales.ws_item_sk = item3.i_item_sk INNER JOIN (SELECT i_manufact_id FROM item WHERE i_category = 'Books') AS t14 ON item3.i_manufact_id = t14.i_manufact_id GROUP BY item3.i_manufact_id) AS t17 GROUP BY i_manufact_id ORDER BY SUM(total_sales) LIMIT 100