SELECT item.i_item_id, item.i_item_desc, item.i_current_price FROM item, inventory, date_dim, catalog_sales WHERE item.i_current_price >= 29 AND item.i_current_price <= 29 + 30 AND (inventory.inv_item_sk = item.i_item_sk AND (date_dim.d_date_sk = inventory.inv_date_sk AND date_dim.d_date >= '2002-03-29')) AND (date_dim.d_date <= (CAST('2002-03-29' AS DATE) + INTERVAL '60' DAY) AND (item.i_manufact_id = 705 OR item.i_manufact_id = 742 OR item.i_manufact_id = 777 OR item.i_manufact_id = 944) AND (inventory.inv_quantity_on_hand >= 100 AND (inventory.inv_quantity_on_hand <= 500 AND catalog_sales.cs_item_sk = item.i_item_sk))) ORDER BY item.i_item_id LIMIT 100