SELECT COUNT(*) FROM (SELECT * FROM (SELECT customer.c_last_name, customer.c_first_name, date_dim.d_date FROM store_sales, date_dim, customer WHERE store_sales.ss_sold_date_sk = date_dim.d_date_sk AND store_sales.ss_customer_sk = customer.c_customer_sk AND date_dim.d_month_seq >= 1189 AND date_dim.d_month_seq <= 1189 + 11 INTERSECT SELECT customer0.c_last_name, customer0.c_first_name, date_dim0.d_date FROM catalog_sales, date_dim AS date_dim0, customer AS customer0 WHERE catalog_sales.cs_sold_date_sk = date_dim0.d_date_sk AND catalog_sales.cs_bill_customer_sk = customer0.c_customer_sk AND date_dim0.d_month_seq >= 1189 AND date_dim0.d_month_seq <= 1189 + 11) AS t INTERSECT SELECT customer1.c_last_name, customer1.c_first_name, date_dim1.d_date FROM web_sales, date_dim AS date_dim1, customer AS customer1 WHERE web_sales.ws_sold_date_sk = date_dim1.d_date_sk AND web_sales.ws_bill_customer_sk = customer1.c_customer_sk AND date_dim1.d_month_seq >= 1189 AND date_dim1.d_month_seq <= 1189 + 11) AS t6 LIMIT 100