SELECT t7.w_warehouse_sk, t7.i_item_sk, t7.d_moy, t7.mean, t7.cov, t16.w_warehouse_sk AS w_warehouse_sk0, t16.i_item_sk AS i_item_sk0, t16.d_moy AS d_moy0, t16.mean AS mean0, t16.cov AS cov0 FROM (SELECT * FROM (SELECT w_warehouse_name, w_warehouse_sk, i_item_sk, d_moy, stdev, mean, CASE WHEN mean = 0 THEN NULL ELSE stdev / mean END AS cov FROM (SELECT t0.w_warehouse_name, t0.w_warehouse_sk, t0.i_item_sk, t1.d_moy, STDDEV_SAMP(t0.inv_quantity_on_hand) AS stdev, AVG(t0.inv_quantity_on_hand) AS mean FROM (SELECT * FROM (SELECT * FROM inventory, item WHERE inventory.inv_item_sk = item.i_item_sk) AS t, warehouse WHERE t.inv_warehouse_sk = warehouse.w_warehouse_sk) AS t0, (SELECT * FROM date_dim WHERE d_year = 2000) AS t1 WHERE t0.inv_date_sk = t1.d_date_sk GROUP BY t0.i_item_sk, t0.w_warehouse_sk, t0.w_warehouse_name, t1.d_moy) AS t4 WHERE CASE WHEN t4.mean = 0 THEN FALSE ELSE t4.stdev / t4.mean > 1 END) AS t6 WHERE d_moy = 1) AS t7, (SELECT * FROM (SELECT w_warehouse_name, w_warehouse_sk, i_item_sk, d_moy, stdev, mean, CASE WHEN mean = 0 THEN NULL ELSE stdev / mean END AS cov FROM (SELECT t9.w_warehouse_name, t9.w_warehouse_sk, t9.i_item_sk, t10.d_moy, STDDEV_SAMP(t9.inv_quantity_on_hand) AS stdev, AVG(t9.inv_quantity_on_hand) AS mean FROM (SELECT * FROM (SELECT * FROM inventory AS inventory0, item AS item0 WHERE inventory0.inv_item_sk = item0.i_item_sk) AS t8, warehouse AS warehouse0 WHERE t8.inv_warehouse_sk = warehouse0.w_warehouse_sk) AS t9, (SELECT * FROM date_dim WHERE d_year = 2000) AS t10 WHERE t9.inv_date_sk = t10.d_date_sk GROUP BY t9.i_item_sk, t9.w_warehouse_sk, t9.w_warehouse_name, t10.d_moy) AS t13 WHERE CASE WHEN t13.mean = 0 THEN FALSE ELSE t13.stdev / t13.mean > 1 END) AS t15 WHERE d_moy = 1 + 1) AS t16 WHERE t7.i_item_sk = t16.i_item_sk AND t7.w_warehouse_sk = t16.w_warehouse_sk ORDER BY t7.w_warehouse_sk, t7.i_item_sk, t7.mean, t7.cov, t16.mean, t16.cov